import React from 'react';
import { useParams } from 'react-router-dom';
import './EditPost.css'
import { supabase } from '../client'

const EditPost = ({data}) => {

    const {id} = useParams();
    const post = data.filter(item => item.id === id)[0];
    const [updatedPost, setUpdatedPost] = React.useState(post);

    const handleChange = (event) => {
        const {name, value} = event.target;
        setUpdatedPost({...updatedPost, [name]: value});
    }

    const updatePost = async (event) => {
        event.preventDefault();
        await supabase
        .from('Posts')
        .update(updatedPost)
        .eq('id', id)

        window.location = '/';
    }

    return (
        <div>
            <form>
                <label for="title">Title</label> <br />
                <input type="text" id="title" name="title" value={updatedPost.title} onChange={handleChange}  /><br />
                <br/>

                <label for="author">Author</label><br />
                <input type="text" id="author" name="author" value={updatedPost.author} onChange={handleChange} /><br />
                <br/>

                <label for="description">Description</label><br />
                <textarea rows="5" cols="50" id="description" value={updatedPost.description} onChange={handleChange} >
                </textarea>
                <br/>
                <input type="submit" value="Submit" />
                <button className="deleteButton">Delete</button>
            </form>
        </div>
    )
}

export default EditPost
